


var net = require('net');
var gotData = 0;




var server = net.createServer(function (commandReceiver){
    console.log("Java client connected...");
    commandReceiver.on('data', function (data)
    {
		gotData = data;
        console.log("Here you go: " + gotData);   
    });
});




server.listen(6500, function () {
    console.log('nodeServer listening port:6000');
});
